// From Ogar

module.exports = {
    Default: require('./Default'),
    Nova: require('./Nova')
};

var get = function(modeId) {
    var mode;
    switch (modeId) {
        case 1: // Nova
            mode = new module.exports.Nova();
            break;
        case 0:
        default: // Default
            mode = new module.exports.Default();
            break;
    }
    return mode;
};

module.exports.get = get;

